# https://leetcode.com/problems/is-subsequence/

# @param {String} s
# @param {String} t
# @return {Boolean}
def is_subsequence(s, t)

  t.chars.each { |i| if !s.include?(i) then t.delete!(i) end }

  p t
  p s == t
    
end
# ------------------------------------------------------------------------------------- #

#is_subsequence("abc", "xahbgdcx")
# true

#is_subsequence("abc", "ahbgdc")
# true

#is_subsequence("axc", "ahbgdc")
# false

is_subsequence("leeeeetcode", "")
# false